    private void btnParpadearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnParpadearActionPerformed
        if(vez==0){
            t.start();
            vez=1;
        }else{
            etiqueta.myResume();
        }
    }//GEN-LAST:event_btnParpadearActionPerformed

    private void btnDetenerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDetenerActionPerformed
        etiqueta.mySuspend();
    }//GEN-LAST:event_btnDetenerActionPerformed

